scripts.startup:
-- x1, y1, x2, y2 player2_flag_zone_quad = {0,player2_flag_location[2]-warning_zone_radius,player2_flag_location[1]+warning_zone_radius,255} player3_flag_zone_quad = {player3_flag_location[1]-warning_zone_radius,player3_flag_location[2]-warning_zone_radius,255,255} player2_flag_small_zone_quad = {player2_flag_location[1]-1,player2_flag_location[2]-1,player2_flag_location[1]+1,player2_flag_location[2]+1} player3_flag_small_zone_quad = {player3_flag_location[1]-1,player3_flag_location[2]-1,player3_flag_location[1]+1,player3_flag_location[2]+1} player2_home_zone_quad = {0,0,startLocation(1)[1]+home_zone_radius,startLocation(1)[2]+home_zone_radius} player3_home_zone_quad = {startLocation(2)[1]-home_zone_radius,0,255,startLocation(2)[2]+home_zone_radius}
-- create player2 units createUnit('castle', 1, startLocation(1)) player2_home = lastCreatedUnit() createUnit('swordman', 1, startLocation(1)) createUnit('archer', 1, startLocation(1)) createUnit('battle_machine', 1, startLocation(1)) disableConsume(1) giveResource('gold', 1, 250); giveResource('wood', 1, 150); giveResource('food', 1, 150);
-- create player3 units createUnit('main_teepee', 2, startLocation(2)) player3_home = lastCreatedUnit() createUnit('stickfighter', 2, startLocation(2)) createUnit('fire_archer', 2, startLocation(2)) createUnit('fire_golem', 2, startLocation(2)) disableConsume(2) giveResource('gold', 2, 250); giveResource('wood', 2, 150); giveResource('housing', 2, 1);
-- create player1 units createUnit('forum', 0, startLocation(0)) for i=1, 3 do createUnit('slave', 0, startLocation(0)) createUnit('archer', 0, startLocation(0)) createUnit('spearman', 0, startLocation(0)) createUnit('cow', 0, startLocation(0)) end createUnit('guard_tower', 0, {startLocation(0)[1]-12,startLocation(0)[2]-10}) createUnit('guard_tower', 0, {startLocation(0)[1]-12,startLocation(0)[2]+12}) createUnit('guard_tower', 0, {startLocation(0)[1]+12,startLocation(0)[2]-8}) createUnit('guard_tower', 0, {startLocation(0)[1]+12,startLocation(0)[2]+12}) giveResource('gold', 0, 250); giveResource('wood', 0, 150); giveResource('food', 0, 150);
player1_in_home2_zone_event = registerCellAreaTriggerEventForFactionToLocation(0, player2_home_zone_quad) player1_in_home3_zone_event = registerCellAreaTriggerEventForFactionToLocation(0, player3_home_zone_quad) player2_in_flag_zone_event = registerCellAreaTriggerEventForFactionToLocation(1, player3_flag_zone_quad) player3_in_flag_zone_event = registerCellAreaTriggerEventForFactionToLocation(2, player2_flag_zone_quad)
-- setup the flags addCellMarker(2,'Catch this flag!','',player2_flag_location) addCellMarker(1,'Catch this flag!','',player3_flag_location) if humanFaction() == 1 then -- addCellMarker(-1,'The flag for capture by green player.','',{player2_flag_location[1]-2, player2_flag_location[2]}) showMarker(500, 1,'The flag for player 2!','',player2_flag_location) showMarker(1000, -1,'The flag for player 3!','',player3_flag_location) end if humanFaction() == 2 then -- addCellMarker(-1,'The flag for capture by blue player.','',{player3_flag_location[1]+2, player3_flag_location[2]}) showMarker(1000, -1,'The flag for player 2!','',player2_flag_location) showMarker(500, 2,'The flag for player 3!','',player3_flag_location) end
player1_in_home_zone = 0 player1_attacker_command = 'GoHome' player2_status = 'None' player3_status = 'None' player2_has_flag_unit = 0 player3_has_flag_unit = 0 flag_bearers_status = 0 destroyed_home_base = 'None' ScenarioStatus = 'Game' showMessage('Brief1', 'CaptureFlag') timer_event1 = startTimerEvent() timer_event2 = startTimerEvent() timer1_status = 'None'
